
public class BooleanDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 boolean p=true;
		 boolean q=false;
		 
		 System.out.println("p is "+p);
		 System.out.println("q is "+q);
		 
		 System.out.println("After changing p & q ");
		 p=false;
		 q=true;
		 
		 System.out.println("p is "+p);
		 System.out.println("q is "+q);
	}

}
